value.css:
*{
    font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
}

.container{
    margin: 0;
    overflow: auto;
}
.header{
    margin-left: 260px;
    margin-top: 100px;
    margin-bottom: 30px;
    font-size: 30px;
}

.sub-container{
    display: flex;
    justify-content: center;
}

img{
    height: 300px;
    /* width: 300px; */
    margin-right: 30px;
   max-width: 300px;
   min-width: 100px;
   transition: width 2s;
}
p{
    text-align: left;
}

.lighter-paragraph{
    color: gray;
    text-transform: uppercase;
    font-size: 11px;
}
.thicker-paragraph{
    font-size: 15px;
    margin-top: 0px;
}
img:hover{
 box-shadow:1px 2px 2px gray;
 width: 320px;
}

@media screen and (max-width:734px) {
   img{
    height: 200px;
    width: 300px;
    margin-right: 30px;
   }

   .header{
   text-align: center;
   margin-left: 0px;
    margin-top: 50px;
    margin-bottom: 20px;
    font-size: 20px;
}



}

