styles.raw:
--gray: #374151;

.primo-copy {
  margin: 0 auto;
  width: 100%;
  padding-right: 2rem;
  padding-left: 2rem;
  font-size: 1.125rem;
  color: #374151;
  text-align:center;

  h1 { 
    font-size: 1.875rem;
    font-weight: 500;
  }

  h2 {
    font-size: 1.5rem;
    font-weight: 500;

  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  ul {
    list-style-type: disc;
    list-style-position: inside;

  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  a {
    color: black;
    text-decoration: underline;
  }
  p{
    text-align:center;
  }
  img{
    height:35px;
  	margin-left: auto;
  	margin-right: auto;
  }


  blockquote {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1.5rem;
  }

  mark {
    --text-opacity: 1;
    color: #161e2e;
    --bg-opacity: 1;
    background-color: #fce96a;
  }
}

@media (min-width: 1024px) {
  .primo-copy h1 {
    font-size: 3rem;
  }

  .primo-copy h2 {
    font-size: 2.25rem;
  }
}

@media (min-width: 640px) {
  .primo-copy {
    max-width: 640px;
  }
}

@media (min-width: 768px) {
  .primo-copy {
    max-width: 768px;
  }
}

@media (min-width: 1024px) {
  .primo-copy {
    max-width: 1024px;
  }
}

@media (min-width: 1280px) {
  .primo-copy {
    max-width: 1280px;
  }
}

@media (min-width: 1536px) {
  .primo-copy {
    max-width: 1536px;
  }
}

